mapper.update:
update t_blood_pressure_setting
high = #{high},
low = #{low},
periodic = #{periodic},
user_id = #{userId}
where id = #{id}

mapper.select:
select t.*,r.time from t_blood_pressure_setting t ,
t_report r where t.user_id = #{user_id} and r.user_id = #{user_id} and
r.time = (select max(time) from t_report where user_id = #{user_id});
